import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';


const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');




  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    const encodedEmail = encodeURIComponent(email);
    try {
      const response = await axios.get(`https://task-manager-backend2.azurewebsites.net/api/Users/GetUser/${encodedEmail}`, {
        auth: {
          username: email,
          password: password,
        }
      });
      console.log(response.data);
      Swal.fire({
        title: 'Bienvenido!',
        text: response.data.name,
        icon: 'success',
        confirmButtonText: 'OK'
      }).then((result) => {
        if (result.isConfirmed) {
          // Aquí pondrías tu lógica para redirigir al dashboard
        }
      });

    } catch (error) {
      console.error(error);

      // Si hay un error, muestra un SweetAlert
      Swal.fire({
        title: 'Error!',
        text: 'Usuario o contraseña incorrectos',
        icon: 'error',
        confirmButtonText: 'Intentar de nuevo'
      });
    }
  };





  return (
<form onSubmit={handleLogin} className="container mt-5">
  <div className="mb-3">
    <label htmlFor="email" className="form-label">
      Email:
    </label>
    <input
      type="email"
      className="form-control"
      id="email"
      value={email}
      onChange={e => setEmail(e.target.value)}
    />
  </div>
  <div className="mb-3">
    <label htmlFor="password" className="form-label">
      Password:
    </label>
    <input
      type="password"
      className="form-control"
      id="password"
      value={password}
      onChange={e => setPassword(e.target.value)}
    />
  </div>
  <button type="submit" className="btn btn-primary">
    Login
  </button>
</form>

  );
};

export default LoginPage;
