import React, { useState } from 'react';
import axios from 'axios';

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.get(`https://task-manager-backend2.azurewebsites.net/api/Users/GetUser/${email}`, {
        auth: {
          username: email,
          password: password,
        }
      });
      console.log(response.data);
      // Aquí deberías manejar la redirección al dashboard o mostrar un mensaje de éxito.
    } catch (error) {
      console.error(error);
      // Maneja el error, como mostrar un mensaje al usuario.
    }
  };

  return (
<form onSubmit={handleLogin} className="container mt-5">
  <div className="mb-3">
    <label htmlFor="email" className="form-label">
      Email:
    </label>
    <input
      type="email"
      className="form-control"
      id="email"
      value={email}
      onChange={e => setEmail(e.target.value)}
    />
  </div>
  <div className="mb-3">
    <label htmlFor="password" className="form-label">
      Password:
    </label>
    <input
      type="password"
      className="form-control"
      id="password"
      value={password}
      onChange={e => setPassword(e.target.value)}
    />
  </div>
  <button type="submit" className="btn btn-primary">
    Login
  </button>
</form>

  );
};

export default LoginPage;
